import { createStore } from "redux";
const initialaccountstate = {
  balance: 0,
  loan: 0,
};

function actionAccountDeposite(amount) {
  return { type: "account/deposite", payload: amount };
}
function actionAccountWithdraw(amount) {
  return { type: "account/withdraw", payload: amount };
}
function actionAccountGetloan(amount) {
  return { type: "account/getLoan", payload: amount };
}

function accountReducer(state = initialaccountstate, action) {
  switch (action.type) {
    case "account/deposite":
      return { ...state, balance: state.balance + action.payload };
    case "account/withdraw":
      return { ...state, balance: state.balance - action.payload };
    case "account/getLoan":
      return {
        ...state,
        balance: state.balance + action.payload,
        loan: action.payload,
      };
    default:
      return state;
  }
}

const store = createStore(accountReducer);

store.dispatch(actionAccountDeposite(100));
store.dispatch(actionAccountWithdraw(20));
store.dispatch(actionAccountGetloan(200));
console.log(store.getState());
